import React, {useState} from "react";
import { Card } from '@material-ui/core';
import { CardMedia } from '@material-ui/core';
import { Button } from '@material-ui/core';
import './Cards.css';

import blank from '../images/blank.png';


const CardComponent = (props) => {

    const [flipped, setFlipped] = useState(false);

    if (! flipped) {
        return <Card className="CardStyleBack" style={{backgroundColor:'white'}}> <Button className="FlipBack"
            onClick={() => {
                setFlipped(flipped => { return !flipped });
            }}
        > <CardMedia
        component="img"
        image={blank}
        alt="blank"
        /></Button>
      </Card>
    }

    return (
        <>
        <Card className="CardStyleFront" style={{backgroundColor:props.color}}> 
        <Button className="FlipFront"
            onClick={() => {
                setFlipped(flipped => { return !flipped });
            }}
        > <CardMedia
        component="img"
        image={props.image}
        alt="arcade"
        /></Button>
       </Card>
       </>

     );
}

export default CardComponent;
