import React, { useMemo } from "react";
import { Card } from '@material-ui/core';
import { CardMedia } from '@material-ui/core';
import { Button } from '@material-ui/core';
import './Cards.css';

import blank from '../images/blank.png';


const CardComponent = (props) => {

    const flipped = props.flipped;
    const matched = props.matched;

    const isFlipped = useMemo(() => {
        return !props.flipped && !props.matched;
    }, [props.flipped, props.matched])

    const toggleFlip = () => {
        props.setCardFlipped(props.id, !flipped);
    }

    if (! flipped && ! matched) {
        return <Card className="CardStyleBack" style={{backgroundColor:'white'}}> 
        <Button className="Button"
            onClick={toggleFlip}
        > <CardMedia className="CardMedia"
        type="blank"
        component="img"
        image={blank}
        alt="blank"
        /></Button>
        </Card>
    }

    return (
        <>
        <Card className="CardStyleFront" style={{backgroundColor:props.color}}> 
        <Button className="Button"
            disabled={matched}
            onClick={toggleFlip}
        > <CardMedia className="CardMedia"
        type={props.type}
        component="img"
        image={props.image}
        alt="arcade"
        /></Button>
       </Card>
       </>

     );
}

export default CardComponent;
