import React, {useState} from "react";
import { Card } from '@material-ui/core';
import { CardMedia } from '@material-ui/core';
import { Button } from '@material-ui/core';
import './Cards.css';

import blank from '../images/blank.png';
import arcade from '../images/arcade.png';
import calcetto from '../images/calcetto.png';
import coding from '../images/coding.png';
import furnace from '../images/furnace.png';
import meeting from '../images/meeting.png';
import party from '../images/party.png';
import pingpong from '../images/pingpong.png';

const cardsArray = [
    {   
        type: 'arcade',
        image: arcade,
        flipped: false,
    },
    {   
        type: 'calcetto',
        image: calcetto,
        flipped: false,
    },
    {   
        type: 'coding',
        image: coding,
        flipped: false,
    },
    {
        type: 'furnace',
        image: furnace,
        flipped: false,
    },
    {
        type: 'meeting',
        image: meeting,
        flipped: false,
    },
    {
        type: 'party',
        image: party,
        flipped: false,
    },
    {
        type: 'pingpong',
        image: pingpong,
        flipped: false,
    },
    {   
        type: 'arcade',
        image: arcade,
        flipped: false,
    },
    {   
        type: 'calcetto',
        image: calcetto,
        flipped: false,
    },
    {   
        type: 'coding',
        image: coding,
        flipped: false,
    },
    {
        type: 'furnace',
        image: furnace,
        flipped: false,
    },
    {
        type: 'meeting',
        image: meeting,
        flipped: false,
    },
    {
        type: 'party',
        image: party,
        flipped: false,
    },
    {
        type: 'pingpong',
        image: pingpong,
        flipped: false,
    },
];

const CardComponent = () => {

    const [flipped, setFlipped] = useState(false);

    if (! flipped) {
        return <Card className="CardStyle" style={{backgroundColor:'white'}}> <Button
            onClick={() => {
                setFlipped(flipped => { return !flipped });
            }}
        > <CardMedia
        component="img"
        image={blank}
        /></Button>
      </Card>
    }

    return ( 
        <Card className="CardStyle" style={{backgroundColor:'#f8d06b'}}> <Button
            onClick={() => {
                setFlipped(flipped => { return !flipped });
            }}
        > <CardMedia
        type={cardsArray.type}
        component="img"
        image={cardsArray.image}
        /></Button>
      </Card>
     );
}

export default CardComponent;
