import React from "react";
import { Card } from '@material-ui/core';
import { CardMedia } from '@material-ui/core';
import { Button } from '@material-ui/core';
import './Cards.css';

import blank from '../images/blank.png';


const CardComponent = (props) => {

    const flipped = props.flipped;
    const matched = props.matched;

    const toggleFlip = () => {
        props.setCardFlipped(props.id, !flipped);
    }

    if (matched) {
        return <Card className="CardStyleFront" style={{backgroundColor:props.color}} > 
            <Button 
                disabled
                onClick={toggleFlip}
            > <CardMedia style={{transition: 'transform 0.6s', transform: 'rotateY(180deg)',transitionDuration: '2s',
    transformStyle: 'preserve-3d'}}
            type={props.type}
            component="img"
            image={props.image}
            alt="arcade"
            /></Button>
       </Card>
    }

    if (! flipped) {
        return <Card className="CardStyleBack" style={{backgroundColor:'white'}}> 
        <Button 
            onClick={toggleFlip}
        > <CardMedia 
        type="blank"
        component="img"
        image={blank}
        alt="blank"
        /></Button>
      </Card>
    }

    return (
        <>
        <Card className="CardStyleFront" style={{backgroundColor:props.color}}> 
        <Button
            onClick={toggleFlip}
        > <CardMedia
        type={props.type}
        component="img"
        image={props.image}
        alt="arcade"
        /></Button>
       </Card>
       </>

     );
}

export default CardComponent;
