import React, {useState} from "react";
import './Cards.css';
import CardFlip  from './CardFlip.js';
import contents from './contents.js';


const Cards = () => {

    if(content.type === content.type) {
    return (
        <>
        {contents.map((content)=>{
          return <CardFlip 
            type={content.type}
            image={content.image}
            flipped={content.flipped}
            color={content.color}
          /> 
        })}
        </>
    );
 } 
} 

export default Cards;
