import React, {useState} from "react";
import { Card } from '@material-ui/core';
import { CardMedia } from '@material-ui/core';
import { Button } from '@material-ui/core';
import './Cards.css';
import arcade from '../images/arcade.png';

const CardComponent = () => {

    const [flipped, setFlipped] = useState(false);

    if (! flipped) {
        return <Card className="CardStyle" style={{backgroundColor:'white'}}> <Button
            onClick={() => {
                setFlipped(flipped => !flipped);
            }}
        > <CardMedia
        component="img"
        alt="arcade"
        /></Button>
      </Card>
    }

    return ( 
        <Card className="CardStyle" style={{backgroundColor:'#f8d06b'}}> <Button> <CardMedia
        component="img"
        image={arcade}
        alt="arcade"
        /></Button>
      </Card>
     );
}

export default CardComponent;
