import * as React from 'react';
import { Card } from '@material-ui/core';
import { CardMedia } from '@material-ui/core';
import { Button } from '@material-ui/core';
import './Cards.css';
import CardFlip  from './CardFlip.js';

import arcade from '../images/arcade.png';
import calcetto from '../images/calcetto.png';
import coding from '../images/coding.png';
import furnace from '../images/furnace.png';
import meeting from '../images/meeting.png';
import party from '../images/party.png';
import pingpong from '../images/pingpong.png';

const Cards = () => {

const arrayCards = [
  {id:1, name: 'arcade'},
  {id:2, name: 'calcetto'},
  {id:3, name: 'coding'},
  {id:4, name: 'furnace'},
  {id:5, name: 'meeting'},
  {id:6, name: 'party'},
  {id:7, name: 'pingpong'},
];

    return (
        <>  
        <CardFlip />
        <Card className="CardStyle" style={{backgroundColor:'#f89b22'}}> <Button> <CardMedia
          component="img"
          id={arrayCards.id}
          image={calcetto}
          alt="calcetto"
          /> 
        </Button>
        </Card>
        <Card className="CardStyle" style={{backgroundColor:'#ea3940'}}> <Button> <CardMedia
          component="img"
          id={arrayCards.id}
          image={coding}
          alt="coding"
          /> </Button>
        </Card>
        <Card className="CardStyle" style={{backgroundColor:'#7cc142'}}> <Button> <CardMedia
          component="img"
          id={arrayCards.id}
          image={furnace}
          alt="furnace"
          /> </Button>
        </Card>
        <Card className="CardStyle" style={{backgroundColor:'#0083b0'}}> <Button> <CardMedia
          component="img"
          id={arrayCards.id}
          image={meeting}
          alt="meeting"
          /> </Button>
        </Card>
        <Card className="CardStyle" style={{backgroundColor:'#a12d90'}}> <Button> <CardMedia
          component="img"
          id={arrayCards.id}
          image={party}
          alt="party"
          /> </Button>
        </Card>
        <Card className="CardStyle" style={{backgroundColor:'#514993'}}> <Button> <CardMedia
          component="img"
          id={arrayCards.id}
          image={pingpong}
          alt="pingpong"
          /> </Button>
        </Card>
        <br/>
        <Card className="CardStyle" style={{backgroundColor:'#514993'}}> <Button> <CardMedia
          component="img"
          id={arrayCards.id}
          image={pingpong}
          alt="pingpong"
          /> </Button>
        </Card>
        <Card className="CardStyle" style={{backgroundColor:'#a12d90'}}> <Button> <CardMedia
          component="img"
          id={arrayCards.id}
          image={party}
          alt="party"
          /> </Button>
        </Card>
        <Card className="CardStyle" style={{backgroundColor:'#0083b0'}}> <Button> <CardMedia
          component="img"
          id={arrayCards.id}
          image={meeting}
          alt="meeting"
          /> </Button>
        </Card>
        <Card className="CardStyle" style={{backgroundColor:'#7cc142'}}> <Button> <CardMedia
          component="img"
          id={arrayCards.id}
          image={furnace}
          alt="furnace"
          /> </Button>
        </Card>
        <Card className="CardStyle" style={{backgroundColor:'#ea3940'}}> <Button> <CardMedia
          component="img"
          id={arrayCards.id}
          image={coding}
          alt="coding"
          /> </Button>
        </Card>
        <Card className="CardStyle" style={{backgroundColor:'#f89b22'}}> <Button> <CardMedia
          component="img"
          id={arrayCards.id}
          image={calcetto}
          alt="calcetto"
          /> </Button>
        </Card>
        <Card className="CardStyle" style={{backgroundColor:'#f8d06b'}}> <Button onClick={() => {
          console.log('clicked');}}> <CardMedia
          component="img"
          id={arrayCards.id}
          image={arcade}
          alt="arcade"
          /> </Button>
        </Card>
        </>
    );
}

export default Cards;
