import React, {useState} from "react";
import { Card } from '@material-ui/core';
import { CardMedia } from '@material-ui/core';
import { Button } from '@material-ui/core';
import './Cards.css';
import cardsArray from "./Content";

import arcade from '../images/arcade.png';
import calcetto from '../images/calcetto.png';
import coding from '../images/coding.png';
import furnace from '../images/furnace.png';
import meeting from '../images/meeting.png';
import party from '../images/party.png';
import pingpong from '../images/pingpong.png';
import blank from '../images/blank.png';

const CardComponent = (props) => {

    const [flipped, setFlipped] = useState(false);
    const [image, setImage] = useState(0);

    if (! flipped) {
        return <Card className="CardStyle" style={{backgroundColor:'white'}}> <Button
            onClick={() => {
                setFlipped(flipped => { return !flipped });
            }}
        > <CardMedia
        component="img"
        image={blank}
        alt="arcade"
        /></Button>
      </Card>
    }

    return (
        <>
        <Card className="CardStyle" style={{backgroundColor:'#f8d06b'}}> <Button
            onClick={() => {
                setFlipped(flipped => { return flipped });
            }}
        > <CardMedia
        component="img"
        image={setImage.state[image.srate]}
        alt="arcade"
        /></Button>
       </Card>
       </>
     );
}

export default CardComponent;
