import React, {useState} from "react";
import { Card } from '@material-ui/core';
import { CardMedia } from '@material-ui/core';
import { Button } from '@material-ui/core';
import './Cards.css';
import Content from "./Content.js";

import arcade from '../images/arcade.png';
import blank from '../images/blank.png';
import cardsArray from "./Content.js";

const CardComponent = (props) => {

    const [flipped, setFlipped] = useState(false);

    if (! flipped) {
        return <Card className="CardStyle" style={{backgroundColor:'white'}}> <Button
            onClick={() => {
                setFlipped(flipped => { return !flipped });
            }}
        > <CardMedia
        component="img"
        image={blank}
        type="arcade"
        /></Button>
      </Card>
    }

    return (
        <>
        {cardsArray.map((type,image) => {
        return <Card className="CardStyle" style={{backgroundColor:'#f8d06b'}}> <Button
            onClick={() => {
                setFlipped(flipped => { return flipped });
            }}
        > <CardMedia
        component="img"
        image={cardsArray.image}
        type={cardsArray.type}
        /></Button>
       </Card>
        })} 
       </>
     );
}

export default CardComponent;
